(function() {
  var os;

  os = require('os');

  module.exports = {
    system: function(cb) {
      var util;
      if (typeof cb !== 'function') {
        cb = (function() {});
      }
      util = {
        total: os.totalmem(),
        free: os.freemem()
      };
      cb(null, util);
      return util;
    },
    process: function(cb) {
      var util;
      if (typeof cb !== 'function') {
        cb = (function() {});
      }
      util = process.memoryUsage();
      cb(null, util);
      return util;
    }
  };

}).call(this);
