(function() {
  var os;

  os = require('os');

  module.exports = {
    system: function(cb) {
      var time;
      if (typeof cb !== 'function') {
        cb = (function() {});
      }
      return cb(null, time = os.uptime());
    },
    process: function(cb) {
      var time;
      if (typeof cb !== 'function') {
        cb = (function() {});
      }
      cb(null, time = process.uptime());
      return time;
    }
  };

}).call(this);
