(function() {
  var Checker,
    __bind = function(fn, me){ return function(){ return fn.apply(me, arguments); }; };

  Checker = (function() {
    function Checker(_fn) {
      this._fn = _fn;
      this.backend = __bind(this.backend, this);
    }

    Checker.prototype.backend = function(cb) {
      if (this._fn.length === 1) {
        return this._fn(function(val) {
          return cb(!!val);
        });
      } else {
        return cb(!!this._fn());
      }
    };

    return Checker;

  })();

  module.exports = Checker;

}).call(this);
